import { createStore } from 'vuex'

export default createStore({
  namespace:true,
  state: {
    userInfo: localStorage.getItem('loginData') && JSON.parse(localStorage.getItem('loginData'))
  },
  getters: {
    getStatus(){
      return this.userInfo;
    }
  },
  mutations: {
    setStatus(userInfo){
      this.userInfo=userInfo;
    },
  },
  actions: {
  },
  modules: {
  }
})
